window.addEventListener("elementor/frontend/init",()=>{class e{constructor(e){this.$element=e,this.expand=this.getElementSetting("expand_style")||"hover",this.accordionItems=this.$element.querySelectorAll(".accordion-item"),this.bindEvents()}getElementSetting(e){try{return JSON.parse(this.$element.dataset.settings||"{}")[e]}catch{return null}}bindEvents(){this.accordionItems.forEach((e,t)=>{"click"===this.expand?e.addEventListener("click",()=>this.openAccordion(t,e)):(e.addEventListener("mouseenter",()=>this.openAccordion(t,e)),e.addEventListener("mouseleave",()=>{e.classList.remove("accordion-hover-active")}))})}openAccordion(e,t){this.accordionItems.forEach(e=>{e.classList.toggle("accordion-hover-active",e===t)})}}elementorFrontend.hooks.addAction("frontend/element_ready/wcf--image-accordion.default",t=>new e(t[0]||t))});